import React, { useState } from "react";
import "./App.css";

function App() {
  // State to keep track of system status
  const [systemStatus, setSystemStatus] = useState("Stopped");

  // Function to handle starting the system
  const handleStart = () => {
    setSystemStatus("Running");
  };

  // Function to handle stopping the system
  const handleStop = () => {
    setSystemStatus("Stopped");
  };

  return (
    <div className="container">
      <header className="header">
        <h1>Dashboard - Josh's Hydroponic System</h1>
      </header>
      <aside className="sidebar">
        <h2>Sidebar</h2>
        <p>Links and content go here.</p>
      </aside>
      <main className="main">
        {/* Widget 1: System Control Panel */}
        <div className="card">
          <h3>System Control Panel</h3>
          <button onClick={handleStart}>Start System</button>
          <button onClick={handleStop}>Stop System</button>
          <p>System Status: {systemStatus}</p>
        </div>

        {/* Placeholder for Widget 2 */}
        <div className="card">
          <h3>Widget 2 Placeholder</h3>
          <p>Content Coming Soon</p>
        </div>

        {/* Placeholder for Widget 3 */}
        <div className="card">
          <h3>Widget 3 Placeholder</h3>
          <p>Content Coming Soon</p>
        </div>

        {/* Placeholder for Widget 4 */}
        <div className="card">
          <h3>Widget 4 Placeholder</h3>
          <p>Content Coming Soon</p>
        </div>

        {/* Placeholder for Widget 5 */}
        <div className="card">
          <h3>Widget 5 Placeholder</h3>
          <p>Content Coming Soon</p>
        </div>

        {/* Placeholder for Widget 6 */}
        <div className="card">
          <h3>Widget 6 Placeholder</h3>
          <p>Content Coming Soon</p>
        </div>
      </main>
    </div>
  );
}

export default App;
